import React, { useState, useRef } from 'react';
import {
  SafeAreaView,
  View,
  Text,
  Image,
  TextInput,
  TouchableOpacity,
  FlatList,
  Dimensions,
  Animated,
} from 'react-native';
import styled from 'styled-components/native';

import { Button } from '@ant-design/react-native';
import { AntDesign } from '@expo/vector-icons';

const { width } = Dimensions.get('window');

const Container = styled(SafeAreaView)`
  flex: 1;
  background-color: #fff;
`;

const Header = styled(View)`
  flex-direction: row;
  align-items: center;
  justify-content: space-between;
  padding: 10px;
`;

const Title = styled(Text)`
  font-size: 20px;
  font-weight: bold;
`;

const MenuButton = styled(TouchableOpacity)`
  padding: 5px;
`;

const MenuContent = styled(Animated.View)`
  position: absolute;
  top: 0px; /* Adjust the position based on your UI */
  left: 10px;
  background-color: #fff;
  border-radius: 20px;
  padding: 10px;
  z-index: 1; /* Ensure the menu is above everything */
`;

const MenuItem = styled(TouchableOpacity)`
  padding: 10px;
`;

const MenuItemText = styled(Text)`
  font-size: 16px;
`;

const CartButton = styled(TouchableOpacity)`
  padding: 5px;
`;

const SearchArea = styled(View)`
  padding: 10px;
`;

const Categories = styled(View)`
  flex-direction: row;
  justify-content: space-around;
  padding: 10px;
`;

const SearchInput = styled(TextInput)`
  background-color: #f0f0f2;
  border-radius: 20px;
  padding: 10px;
  font-size: 16px;
  border: solid 1px grey;
`;

const CategoryButton = styled(Button)`
  flex: 1;
  margin: 0 5px;
  background-color: ${props => props.isSelected ? '#ccc' : 'transparent'};
`;

const ProductCard = styled(View)`
  background-color: #fff;
  border-radius: 5px;
  padding: 10px;
  margin: 10px;
  width: ${width * 0.4}px;
`;

const ProductImage = styled(Image)`
  width: 100%;
  height: 150px;
  border-radius: 5px;
`;

const ProductTitle = styled(Text)`
  font-size: 18px;
  font-weight: bold;
  margin-top: 10px;
`;

const ProductPrice = styled(Text)`
  font-size: 16px;
  color: #333;
  margin-top: 5px;
`;

const ScrollButton = styled(TouchableOpacity)`
  align-items: center;
  justify-content: center;
  padding: 10px;
`;

const products = [
  { id: '1', title: 'Stylish Chair', category: 'Chair', price: '$150.99', imageUrl: require('../assets/Blackchair.png') },
];

const SecondPage = () => {
  const flatListRef = useRef();
  const [currentFirstIndex, setCurrentFirstIndex] = useState(0);
  const [searchTerm, setSearchTerm] = useState('');
  const [selectedCategory, setSelectedCategory] = useState('');
  const [isMenuOpen, setIsMenuOpen] = useState(false);
  const menuAnimation = useRef(new Animated.Value(-300)).current; // Initial position for animation

  const toggleMenu = () => {
    setIsMenuOpen(!isMenuOpen);
    Animated.spring(menuAnimation, {
      toValue: isMenuOpen ? -300 : 0, // Slide in or out based on menu state
      useNativeDriver: true,
    }).start();
  };

  const handleScrollNext = () => {
    const nextIndex = currentFirstIndex + 1;
    if (nextIndex < products.length) {
      flatListRef.current.scrollToIndex({ animated: true, index: nextIndex });
      setCurrentFirstIndex(nextIndex);
    }
  };

  const filteredProducts = products.filter(product => {
    return product.title.toLowerCase().includes(searchTerm.toLowerCase()) && (selectedCategory === '' || product.category === selectedCategory);
  });

  const handleCategorySelect = category => {
    setSelectedCategory(category);
    setCurrentFirstIndex(0);
    flatListRef.current.scrollToOffset({ animated: true, offset: 0 });
  };

  return (
    <Container>
      <Header>
        <MenuButton onPress={toggleMenu}>
          <AntDesign name="menu-fold" size={24} color="black" />
        </MenuButton>

        <CartButton>
          <AntDesign name="shoppingcart" size={24} color="black" />
        </CartButton>

      </Header>
      <Title>Vind moderne meubels voor jou</Title>
      <Animated.View style={{ zIndex: isMenuOpen ? 2 : 0, transform: [{ translateX: menuAnimation }] }}>
        <MenuContent>
          <MenuItem>
            <MenuItemText>Stoelen</MenuItemText>
          </MenuItem>
          <MenuItem>
            <MenuItemText>Zetels</MenuItemText>
          </MenuItem>
          <MenuItem>
            <MenuItemText>buitenstoelen</MenuItemText>
          </MenuItem>

        </MenuContent>
      </Animated.View>
      <SearchArea>
        <SearchInput
          placeholder="Search furniture"
          onChangeText={text => setSearchTerm(text)}
          value={searchTerm}
        />
      </SearchArea>
      <Categories>
        <CategoryButton isSelected={selectedCategory === 'Chair'} type="ghost" onPress={() => handleCategorySelect('Chair')}>
          Chair
        </CategoryButton>
        <CategoryButton isSelected={selectedCategory === 'Sofa'} type="ghost" onPress={() => handleCategorySelect('Sofa')}>
          Sofa
        </CategoryButton>
        <CategoryButton isSelected={selectedCategory === 'Table'} type="ghost" onPress={() => handleCategorySelect('Table')}>
          Table
        </CategoryButton>
        <CategoryButton isSelected={selectedCategory === 'Lamp'} type="ghost" onPress={() => handleCategorySelect('Lamp')}>
          Lamp
        </CategoryButton>
      </Categories>
      <View>
        <FlatList
          ref={flatListRef}
          horizontal
          data={filteredProducts}
          renderItem={({ item }) => (
            <ProductCard key={item.id}>
              <ProductImage source={item.imageUrl} />
              <ProductTitle>{item.title}</ProductTitle>
              <ProductPrice>{item.price}</ProductPrice>
            </ProductCard>
          )}
          keyExtractor={item => item.id}
          showsHorizontalScrollIndicator={false}
          snapToAlignment="start"
          snapToInterval={width * 0.4 + 20}
        />
        <ScrollButton onPress={handleScrollNext}>
          <AntDesign name="right" size={24} color="black" />
        </ScrollButton>
      </View>
    </Container>
  );
};

export default SecondPage;
